mapper.select:
SELECT EXISTS(
SELECT 1
FROM contests
WHERE name = #{name}
AND removed_at IS NULL
)

SELECT id AS contestId, name, theme, start_date AS startDate, end_date AS endDate, status, created_by_user_id AS createdByUserId, removed_at AS removedAt FROM contests WHERE 1=1
AND removed_at IS NULL
AND status = #{status}
AND (name ILIKE '%' || #{keyword} || '%' OR theme ILIKE '%' || #{keyword} || '%')
ORDER BY created_at DESC LIMIT #{size} OFFSET #{offset}
