mapper.select:
SELECT EXIST (
  SELECT 1
  FROM photo_embeddings
  WHERE photo_type = 'USER'
    AND status = 'READY'
    AND contest_id = #{contestId}
    AND photo_id = #{userPhotoId}
)

mapper.delete:
DELETE FROM photo_embeddings
WHERE contest_id = #{contestId}
AND photo_type = 'USER'
AND photo_id = #{userPhotoId}

DELETE FROM photo_embeddings
WHERE contest_id = #{contestId}
AND photo_type = 'MODEL'
AND photo_id = #{modelPhotoId}
