mapper.update:
UPDATE user_profiles up
SET total_contests_entered = total_contests_entered + 1,
updated_at = now()
WHERE up.user_id = #{userId}
AND EXISTS (
SELECT 1
FROM contests c
WHERE c.id = #{contestId}
)
AND NOT EXISTS (
SELECT 1
FROM user_photos uph
WHERE uph.user_id = #{userId}
AND uph.contest_id = #{contestId}
)

UPDATE user_profiles
SET profile_image_url = #{imageUrl}
WHERE user_id = #{userId}

UPDATE user_profiles
SET bio = #{bio}
WHERE user_id = #{userId}
